import 'package:tiomusic/l10n/flash_cards/flash_cards_localization.dart';

mixin GermanFlashCards on Object implements FlashCardsLocalization {
  String get categoryCulture => 'Kultur';
  String get categoryJournaling => 'Tagebuch';
  String get categoryMixUp => 'Abwechslung';
  String get categoryPracticingTactics => 'Übungstaktik';
  String get categoryRelaxation => 'Entspannung';
  String get categorySelfCare => 'Selbstfürsorge';
  String get categoryTeam => 'Team';
  String get categoryVision => 'Vision';

  // --- Relaxation ---
  String get descriptionCloseEyesReleaseTension => 'nimm dein Instrument auf und schließe die Augen. Spüre deinen ganzen Körper, von den Zehen bis zum Scheitel, und löse unnötige Spannungen. Baue Pausen in dein Stück ein, um dich zu entspannen.';
  String get descriptionDaysOffCongratulations => 'überlege dir, wie viele freie Tage du bräuchtest, bevor du wieder üben wolltest. Definiere ab jetzt diese Anzahl an Tagen als freie Tage. Für jeden freien Tag, an dem du trotzdem übst, hast du deinen Plan übertroffen — herzlichen Glückwunsch!';
  String get descriptionMeditateFiveMinBefore => 'meditiere 5 Minuten lang im Übungsraum, bevor du anfängst, dein Instrument zu spielen.';
  String get descriptionBreakWaterBeauty => 'nimm alle 10 Minuten eine kurze Pause, um einen Schluck Wasser zu trinken und etwas in deiner Umgebung zu entdecken, das du schön findest.';
  String get descriptionBreathOutThreeTimes => 'bevor du den Übungsraum betrittst, atme dreimal so tief aus, wie du kannst.';
  String get descriptionAllowNoAccomplishment => 'erlaube dir, beim Üben nichts leisten zu müssen. Was würdest du mit deinem Instrument tun, wenn du die Ziele deines Alltags nicht verfolgen müsstest? Tu das zuerst. Denk daran: Alles, was du tust, musst du nicht tun – denn du musst gar nichts.';
  String get descriptionListenFirstThenDecide => 'höre dir dein Stück zuerst an. Entscheide dann, ob du es am Instrument ausprobieren oder heute nur zuhören möchtest. Du kannst es auch mehrmals anhören.';
  String get descriptionReduceStressShakeStompLieDown => 'baue Stress ab, indem du 5 Minuten lang schüttelst, stampfst oder dich hinlegst. Tu intuitiv, was dir in den Sinn kommt.';
  String get descriptionEyeScanSixtySeconds => 'blicke 60 Sekunden lang so schnell wie möglich (nur) mit den Augen umher.';
  String get descriptionHumAndFeelSounds => 'summe zwischendurch Töne und spüre sie körperlich.';
  String get descriptionSitAndDoNothingFiveMinutes => 'setze dich zuerst in den Übungsraum und tue 5 Minuten lang nichts.';
  String get descriptionEyeExerciseDirections => 'mache diese Augenübung: Schaue mehrmals nach links, rechts, oben und unten. Deine Augen werden es dir danken!';
  String get descriptionPayAttentionToBreathing => 'achte besonders auf deine Atmung.';
  String get descriptionRecordPhraseWatchMovements => 'nimm eine Phrase als Video auf und achte beim Anschauen auf unnötige Bewegungen direkt vor dem Einsatz.';
  String get descriptionRecordPlayingWatchFacialExpressions => 'nimm dein Spiel als Video auf und achte beim Anschauen besonders auf deine Mimik während und zwischen den Passagen.';
  String get descriptionListenToBreathingThreeMinutes => 'nimm dir vorher 3 Minuten Zeit und höre deiner eigenen Atmung zu.';
  String get descriptionPayAttentionHeartbeat => 'achte auch auf deinen Herzschlag.';
  String get descriptionSqueezeMusclesTenSeconds => 'lege dich auf den Boden und spanne alle Muskeln gleichzeitig 10 Sekunden lang so stark wie möglich an. Spürst du eine Veränderung zum Ausgangszustand?';

  // --- Team ---
  String get descriptionAskSomeoneToObserveYou => 'bitte jemanden, dich beim Üben zu beobachten. Welchen Rat kann er oder sie dir geben?';
  String get descriptionDescribeGoalTogether => 'beschreibe jemandem ein Ziel und überlegt gemeinsam, wie ihr es erreichen könnt.';
  String get descriptionLetSomeoneElseLead => 'überlasse jemand anderem die Führung. Er oder sie wird dich anleiten, indem er oder sie Anweisungen gibt oder demonstriert, wie du üben kannst.';
  String get descriptionPlayWithDroneSupport => 'bitte jemanden, während du übst, den Grundton der Phrase als Bordunton zu spielen. Spiele dein Stück mit offenen Ohren.';
  String get descriptionTellObstacleAndBrainstorm => 'erzähle jemandem von einem deiner Hindernisse und sucht gemeinsam nach Lösungen.';
  String get descriptionWatchSomeonePracticeWriteInspiration => 'sieh jemand anderem beim Üben zu und schreibe auf, was dich inspiriert.';
  String get descriptionPlayChamberMusicTogether => 'bitte jemanden (den du magst), mit dir Kammermusik zu machen.';
  String get descriptionPlayWithSomeone => 'spiele dein Stück mit jemandem zusammen.';
  String get descriptionChatAboutAversionToPracticing => 'tausche dich mit jemandem darüber aus, was du tun kannst, wenn du eine Abneigung gegen das Üben spürst.';
  String get descriptionHandsOnShouldersReleaseTension => 'lass jemand anderem die Hände auf deine Schultern legen. Achtet gemeinsam darauf, wo du noch Spannung loslassen kannst.';
  String get descriptionAskDistractionWhilePlaying => 'bitte jemanden, dich während des Spielens abzulenken.';
  String get descriptionAskFastForwardDuringPlayback => 'bitte jemanden, beim Mitspielen eine Stelle vorzuspulen.';
  String get descriptionSplitHandsOrPressKeysTogether => 'je nach Instrument: Bitte jemanden, die Tasten zu drücken, während du hineinbläst, oder lass eine Hand von jemand anderem spielen, während du die andere spielst.';
  String get descriptionListenWithoutCommentFifteenMinutes => 'bitte jemanden, dir 15 Minuten lang zuzuhören, ohne zu kommentieren.';
  String get descriptionTellAFriendAndAskForTips => 'erzähle einer Freundin oder einem Freund, was und wie du heute geübt hast. Hat sie/er Tipps für dich?';
  String get descriptionPracticeWithFriend => 'übe gemeinsam mit einer Freundin oder einem Freund.';
  String get descriptionSendVideoToFriend => 'nimm ein Video von dir auf und schicke es an eine Freundin oder einen Freund.';
  String get descriptionPlayForSomeoneYouLike => 'spiele ein Stück oder Lied, das du gut kennst, für jemanden, den du magst.';

  // --- Self-Care ---
  String get descriptionStartWithPhysicalExercises => 'beginne dein Üben mit körperlichen Übungen: Klopfmassage, Nacken kneten, Erdung, Dehnung.';
  String get descriptionExpressHowYouFeel => 'frage dich: Wie fühle ich mich? Drücke dieses Gefühl auf deinem Instrument oder mit deiner Stimme aus. Improvisiere, mache einfach Klänge oder spiele ein passendes Stück.';
  String get descriptionExpressWhatYouWantToGive => 'frage dich: Was möchte ich ausdrücken? Wer bin ich, und was möchte ich der Welt geben? Verkörpere dies spontan und intuitiv auf deinem Instrument oder mit deiner Stimme. Improvisiere, mache Klänge oder spiele ein passendes Stück.';
  String get descriptionTenSecondBreakIfWorse => 'mache 10 Sekunden Pause, wenn du merkst, dass die Stelle, an der du arbeitest, wieder schlechter wird. (Dein Gehirn lernt in den Pausen!)';
  String get descriptionBodyPartsAwarenessList => 'achte auf deinen Körper: Schreibe Körperteile (Nacken, Schultern, Bauch, Füße, Wirbelsäule ...) auf und richte während einer Phrase nacheinander die Aufmerksamkeit auf die Bereiche.';
  String get descriptionBookForTonight => 'überlege, welches Buch du heute Abend vor dem Schlafengehen lesen möchtest.';
  String get descriptionBookToHelpPractice => 'überlege, welches Buch dir beim Üben am meisten helfen könnte. Besorge es dir und lies es.';
  String get descriptionPlanIdealBedtime => 'plane deine ideale Schlafenszeit für heute.';
  String get descriptionPlanFinishWorkToday => 'plane, wann du heute mit der Arbeit aufhören möchtest.';
  String get descriptionPracticeWithoutDevices => 'überlege, wie du deine Übungszeit ohne digitale Geräte gestalten kannst.';
  String get descriptionDrinkHalfLiterWater => 'trinke vorher einen halben Liter Wasser.';
  String get descriptionSleepInIfPossible => 'frage dich, ob du heute ausschlafen konntest. Wenn nicht, versuche es morgen.';
  String get descriptionBiteIntoLemon => 'beiße zuerst in eine Zitrone.';
  String get descriptionGingerShotOrRaw => 'trinke einen Ingwer-Shot oder iss rohen Ingwer.';
  String get descriptionGuidedMeditationBeforehand => 'mache vorher eine geführte Meditation.';
  String get descriptionWhichMealEnergizes => 'überlege, welches Essen dir Energie gibt und welches dich zum Üben zu müde macht.';
  String get descriptionStartDayWithExercise => 'überlege, ob du den Tag mit Bewegung begonnen hast. Wenn nicht, versuche es morgen.';
  String get descriptionWalkWithoutDevices => 'mache einen Spaziergang ohne elektronische Geräte. Dein Gehirn braucht Langeweile, um den Kopf frei zu bekommen. Lass deine Gedanken fließen und beobachte sie einfach.';
  String get descriptionPrepareHoursBeforePerformance => 'überlege, wie du dir schon mehrere Stunden vor deinem nächsten Auftritt Zeit für dich nehmen kannst, damit du dich wohl fühlst.';
  String get descriptionMeetNeedsBeforeOrDuringBreaks => 'sorge dafür, dass deine Bedürfnisse vor dem Üben erfüllt sind – oder erfülle sie in den Pausen.';
  String get descriptionNoticeHeadIsFullTakeBreak => 'merke, wann dein Kopf voll ist, und mache dann eine Pause.';
  String get descriptionPlanDayOffInCalendar => 'plane, wann du einen freien Tag nimmst (Empfehlung: einmal pro Woche), und trage ihn in deinem Kalender rot ein.';
  String get descriptionSwitchToAirplaneMode => 'schalte zuerst dein Smartphone in den Flugmodus oder das Internet aus.';
  String get descriptionWriteDownExtraneousThoughts => 'schreibe alle ablenkenden Gedanken vor und während des Übens auf.';
  String get descriptionStartWithLongNotes => 'beginne mit sehr langen Tönen (Schlagzeug: Wirbel). Mindestens einer sollte dynamisch konstant sein, einer mit Crescendo, einer mit Diminuendo und einer erst ansteigend und dann wieder abfallend.';
  String get descriptionEndWithFavoritePiece => 'spiele zum Schluss dein Lieblingsstück oder -lied.';
  String get descriptionHalfwayFiveMinuteBreak => 'mache zur Hälfte deiner Übezeit eine 5-Minuten-Pause und nutze sie, um z. B. ein Glas Wasser zu trinken, frische Luft zu schnappen oder dich zu bewegen.';
  String get descriptionTidyRoomIfPossible => 'sieh dich um: Ist dein Raum ordentlich? Wenn nicht, kannst du mit wenig Aufwand aufräumen?';
  String get descriptionConsiderEffectivePracticeLocation => 'überlege, wo du effektiv üben kannst – zu Hause oder anderswo? Übst du lieber am gleichen Ort oder profitierst du von Abwechslung?';
  String get descriptionSeeHowYouCanHaveFun => 'schau, wie du Spaß haben kannst.';

  // --- Vision ---
  String get descriptionLieDownUntilMotivated => 'lege dich hin und bleibe dort, bis du genau weißt, warum es sich für dich lohnt aufzustehen und dein Instrument in die Hand zu nehmen.';
  String get descriptionMirrorMotivationalSpeech => 'stelle dich vor einen Spiegel und halte dir selbst eine motivierende Rede: Was hast du schon erreicht, worauf bist du stolz, warum ist dieser Weg richtig für dich und was lässt deine Augen leuchten?';
  String get descriptionPracticeWithGoalInMind => 'denke vorher über dein Ziel nach und übe mit diesem Ziel vor Augen.';
  String get descriptionThinkWhyGetUpEarly => 'überlege, warum du morgen früh aufstehen würdest. Was wäre so belebend, dass du vor dem Wecker aufwachst, weil du so begeistert bist?';
  String get descriptionWriteDownDailyGoal => 'schreibe ein Tagesziel auf – etwas, das du leicht erreichen kannst, und versuche es in der ersten Tageshälfte zu schaffen. Die zweite Tageshälfte fühlt sich dann freier an.';
  String get descriptionWriteDownEuphoricMoments => 'notiere dir euphorische Schlüsselmomente aus der Vergangenheit. Haben sie etwas gemeinsam? Was genau hat deine Augen zum Leuchten gebracht?';

// --- Culture ---
  String get descriptionBookConcertTicket => 'buche ein Ticket für ein Konzert, auf das du dich richtig freust!';
  String get descriptionCreatePrivateConcert => 'gestalte dein eigenes Privatkonzert, indem du eine großartige Aufnahme eines für dich wichtigen Stücks anschaust oder anhörst. Mach es dir gemütlich und schaffe dir eine Wohlfühl-Oase.';
  String get descriptionDiscoverNewPieceInManyWays => 'entdecke ein dir neues Stück auf möglichst viele Arten – höre es, bewege dich dazu, lies den Entstehungs- oder Aufführungskontext, zeichne ein Bild, improvisiere über ein Motiv...';
  String get descriptionListenToRoleModelAndLearn => 'höre Musik einer deiner Vorbilder und schreibe auf, was du von dieser Person lernen möchtest.';
  String get descriptionPlanMeetFriendsListenMusic => 'verabrede dich mit Freund:innen zum Musikhören und erzählt euch anschließend eure Lieblingsmomente und was euch begeistert hat.';
  String get descriptionReadBerliozTreatiseSection => "lies – wenn verfügbar – den Abschnitt zu deinem Instrument in Hector Berlioz' „Instrumentationslehre“, in der von Richard Strauss überarbeiteten Ausgabe (1905).";
  String get descriptionReadWikipediaAboutInstrument => 'lies den Wikipedia-Artikel über dein Instrument.';
  String get descriptionRediscoverPieceInManyWays => 'entdecke ein dir bereits bekanntes Stück auf möglichst viele Arten neu – höre es, bewege dich dazu, lies den Entstehungs- oder Aufführungskontext, zeichne ein Bild, improvisiere über ein Motiv...';
  String get descriptionRewardYourself => 'belohne dich mit allem, was dir einfällt.';
  String get descriptionWriteIntroForTwoTargetGroups => 'schreibe eine Einführung zu deinem aktuellen Stück für zwei unterschiedliche Zielgruppen, z. B. Klassikpublikum, Schulklassen, Senior:innen, Fußballfans etc.';
  String get descriptionWriteTwoMinuteIntroduction => 'schreibe eine zweiminütige Einführung zu deinem aktuellen Stück.';

  // --- Mix-Up ---
  String get descriptionAdjectivesForEachSection => 'finde für jeden Abschnitt deines Stücks mindestens ein passendes Adjektiv und schreibe es in die Noten.';
  String get descriptionAlternateBarPlayThink => 'wechsle ab zwischen einem Takt Spielen und den nächsten Takt Denken oder Singen.';
  String get descriptionAllowQuietestPossibleSound => 'lasse den Klang so leise wie möglich aus deinem Instrument entstehen.';
  String get descriptionAnalyzeSectionOfPiece => 'analysiere einen Abschnitt deines Stücks.';
  String get descriptionChangeLocationOrDirection => 'ändere deinen Übungsort: wechsle den Raum oder drehe dich in eine andere Richtung usw.';
  String get descriptionChooseImageComposeToIt => 'wähle ein Bild oder Foto aus und komponiere Musik dazu.';
  String get descriptionChooseThreeNotesPlayQuietly => 'wähle drei Töne und spiele sie so leise wie möglich.';
  String get descriptionChooseThreeToFiveNotesSong => 'wähle drei bis fünf Töne und mache daraus ein Lied.';
  String get descriptionClapRhythmWithMetronome => 'klatsche den Rhythmus einer Phrase mit dem Metronom.';
  String get descriptionColorCodeSections => 'teile das Stück in Abschnitte und kennzeichne sie farblich.';
  String get descriptionComposeWithOnePitch => 'komponiere ein Stück nur mit einem Ton.';
  String get descriptionConductYourPiece => 'dirigiere dein Stück (gern vor dem Spiegel).';
  String get descriptionDanceToYourPiece => 'tanze zu deinem Stück.';
  String get descriptionDarkenRoomOrCoverEyes => 'verdunkle den Raum oder bedecke deine Augen, bevor du beginnst.';
  String get descriptionDividePieceIntoSectionsColor => 'teile das Stück in Abschnitte und kennzeichne sie farblich.';
  String get descriptionDrawEmoticonsForSections => 'zeichne passende Emoticons für die verschiedenen Abschnitte deines Stücks.';
  String get descriptionDrawYourselfInAction => 'zeichne dich und dein Instrument in Aktion. Was fällt dir an Haltung, Proportionen, Details usw. auf?';
  String get descriptionFilmMusicImagineScene => 'stelle dir vor, dein Stück wäre Filmmusik. Wie sähe die Szene aus?';
  String get descriptionFiveMinutePracticeBlocks => 'übe jeweils nur 5 Minuten (Stoppuhr) und ändere danach dein Übungsziel oder die Methode, sodass du jedes Mal etwas anderes lernst.';
  String get descriptionFollowPartWhileListening => 'höre eine Aufnahme deines Stücks und verfolge dabei deine Stimme oder alternativ die Partitur.';
  String get descriptionImproviseOnMainMotifFiveMin => 'improvisiere 5 Minuten lang über das Hauptmotiv deines Stücks.';
  String get descriptionInstallDroneAndPlayOver => 'installiere eine App für einen Bordunton (Drone) und spiele lange Töne, langsame Tonleitern oder Akkorde darüber.';
  String get descriptionListenAndDecideBodyVsSound => 'höre einmal auf deinen Klang und dann auf deinen Körper.';
  String get descriptionListenAndFollowScore => 'höre eine Aufnahme deines Stücks und verfolge dabei deine Stimme oder alternativ die Partitur.';
  String get descriptionListenRoleModelRecordingAndLearn => 'überlege, welches Stück, welche Aufnahme oder welches Lied du gerade hören möchtest. Höre es dreimal und finde heraus, was dich daran anspricht. Versuche die entdeckte Schönheit in deinem aktuellen Stück auszudrücken.';
  String get descriptionMakeFunIdeasListWeek => 'notiere alle Ideen, die das Üben für dich spielerischer machen. Stell dir vor, wie viele Ideen zusammenkommen, wenn du das eine Woche lang wiederholst!';
  String get descriptionMakeNotToDoList => 'schreibe eine Nicht-zu-tun-Liste. Notiere alles, was du heute nicht tun möchtest, und hake es ab, wenn es wirklich nicht passiert ist.';
  String get descriptionPhotocopyCutRearrangePiece => 'kopiere dein aktuelles Stück, schneide es auseinander und setze es neu zusammen.';
  String get descriptionPickPictureComposeToIt => 'wähle ein Bild oder Foto aus und komponiere Musik dazu.';
  String get descriptionPlanFavoriteSportAsReward => 'plane deinen Lieblingssport als Belohnung nach dem Üben ein.';
  String get descriptionPlayAlongIfPlayalongExists => 'recherchiere, ob es ein Play-along zu deinem Stück gibt, und spiele – falls ja – dazu.';
  String get descriptionPlayByEar => 'spiele etwas, das du nur aus dem Gehör kennst.';
  String get descriptionPlayOnTiptoes => 'spiele eine Phrase auf Zehenspitzen.';
  String get descriptionPlayPhraseAtHalfAndDoubleTempo => 'spiele eine Phrase einmal im normalen Tempo, einmal halb so schnell und einmal doppelt so schnell.';
  String get descriptionPlayPieceAtPulseTempo => 'spiele dein Stück im Tempo deines Pulses.';
  String get descriptionPracticeOnOneLegOrBalance => 'spiele oder singe dein Stück auf einem Bein oder auf einem Balance-Board. Was nimmst du wahr?';
  String get descriptionPracticePieceThatHelpsProgress => 'spiele ein Stück, das dich musikalisch voranbringt.';
  String get descriptionPracticeSingingScaleInTune => 'übe, eine Tonleiter sauber zu singen. Spiele den Grundton am Klavier und singe die Tonleiter auf- und abwärts.';
  String get descriptionPracticeSingingTriadInTune => 'übe, einen Dreiklang sauber zu singen. Spiele den Grundton am Klavier und singe den Dreiklang auf- und abwärts.';
  String get descriptionPreferedActivityListIncorporate => 'erstelle eine Liste mit Dingen, die du lieber tun würdest als zu üben. Notiere auch, was dir daran gefällt, und baue es in dein Üben ein!';
  String get descriptionRecordingsListenThreeTimesReflect => 'überlege, welches Stück, welche Aufnahme oder welches Lied du gerade hören möchtest. Höre es dreimal und finde heraus, was dich daran anspricht.';
  String get descriptionReflectedSoundAgainstWall => 'stelle dich – wenn möglich – vor eine Wand, damit du deinen reflektierten Klang direkt hörst.';
  String get descriptionResearchFingerCoordinationExercise => 'recherchiere online eine Finger-Koordinationsübung und lerne sie. Teste nach einer Woche, wie sich das Spielen davor und danach anfühlt.';
  String get descriptionRulesOverboardJustPlayFreely => 'wirf alle Regeln über Bord und spiele heute einfach frei.';
  String get descriptionSecludedPlaceAndLaugh => 'gehe an einen abgeschiedenen Ort (z. B. in den Keller) und lache. Lege dich auf die Seite und lass deinen Bauch wippen. Denke an etwas Lustiges oder höre etwas Lustiges.';
  String get descriptionSelectAnyPracticeTipAndDoIt => 'sieh dir die Übungstipps an und wähle einen aus, der dich anspricht und den du heute machen möchtest.';
  String get descriptionSilentOrWhisperIfSinger => 'spiele eine Passage lautlos oder flüstere sie, wenn du Sänger:in bist.';
  String get descriptionSomeoneYouLikeTreatInstrumentPet => 'behandle dein Instrument wie ein Haustier. Welche Zuneigung würdest du ihm geben? Wie würdest du es nennen?';
  String get descriptionSoundThenBodyAwareness => 'höre einmal auf deinen Klang und dann auf deinen Körper.';
  String get descriptionStandInFrontOfAudienceImagine => 'stelle dir vor, du spielst vor Publikum (Eltern, Konzertsaal usw.).';
  String get descriptionStartAtRandomPlace => 'beginne an einer zufälligen Stelle irgendwo in der Mitte deines Stücks, deiner Phrase oder Passage.';
  String get descriptionStoryAboutCurrentPiece => 'denke dir eine Geschichte zu deinem aktuellen Stück aus. Erzähle sie, wenn du magst, jemandem.';
  String get descriptionTakeBlankPaperWriteAllNotes => 'nimm ein leeres Notenblatt und schreibe alle Töne auf, die du spielen kannst. Wie viele sind es, und wie viele Oktaven decken sie ab? Recherchiere den vorgesehenen Tonumfang deines Instruments. Ist er eher groß oder klein im Vergleich zu anderen Instrumenten?';
  String get descriptionTakePowerNapThirtyMinutes => 'gönne dir einen kurzen Powernap (max. 30 Minuten). Spiele dein Stück im Kopf, während du einschläfst, und lass dich überraschen, was danach beim Üben passiert.';
  String get descriptionTeacherWouldSayAboutPhrases => 'überlege, was deine Lehrkraft zu den Phrasen sagen würde, die du übst.';
  String get descriptionThinkWhatTrulyMakesYouHappyToday => 'überlege, was dich heute wirklich glücklich machen würde, wenn du es erreichst. Versuche, es in 10 Minuten Übezeit zu schaffen! Was brauchst du dafür?';
  String get descriptionThreeNotesSongQuietPossible => 'wähle drei Töne und spiele sie so leise wie möglich.';
  String get descriptionTiptoeQuietLoudDynamicsBeauty => 'spiele durchgehend mit dem schönsten Klang, der dir möglich ist.';
  String get descriptionUseDroneLongTonesScalesChords => 'installiere eine Drone-App und spiele lange Töne, langsame Tonleitern oder Akkorde darüber.';
  String get descriptionWalkInNatureUnderstandPiece => 'mache einen Spaziergang in der Natur. Was kannst du draußen ohne Instrument tun, das dir hilft, dein Stück besser zu verstehen?';
  String get descriptionWatchScoreBeyondYourPart => 'sieh in die Partitur: Was passiert außerhalb deiner Stimme?';
  String get descriptionWhisperOrSilentPassage => 'spiele eine Passage lautlos oder flüstere sie, wenn du Sänger:in bist.';
  String get descriptionWritePhraseFromMemoryCompare => 'schreibe eine Phrase aus dem Gedächtnis auf Notenpapier und vergleiche sie mit dem Original.';
  String get descriptionYesToNoNeinhorn => 'höre „Neinhorn“ von Marc-Uwe Kling oder lies es (z.B. in der Bibliothek!). Wozu möchtest du „Nein“ sagen? Trau dich!';

  // --- Journaling ---
  String get descriptionAfterwardsDocumentLearned => 'dokumentiere anschließend, was du heute gelernt hast oder welche Erkenntnisse du gewonnen hast.';
  String get descriptionDialogueEnjoyPracticing => 'führe einen schriftlichen Dialog mit dir selbst: Was brauchst du, um das Üben zu genießen?';
  String get descriptionDialogueWhyNotPracticing => 'führe einen schriftlichen Dialog mit dir selbst: Warum übst du gerade nicht? Sei freundlich und verständnisvoll zu dir und finde eine gesunde Lösung. Was brauchst du, um das Üben zu genießen?';
  String get descriptionDocumentEverythingPracticed => 'schreibe alles auf, was du geübt hast.';
  String get descriptionGradePracticeQualityOneToTen => 'bewerte die Qualität deines Übens auf einer Skala von 1 bis 10.';
  String get descriptionGradePracticeSatisfactionResistance => 'bewerte deine Übe-Zufriedenheit und deinen Widerstand, mit dem Üben zu beginnen.';
  String get descriptionListTasksByPriority => 'liste deine Aufgaben nach Priorität auf und übe von wichtig nach unwichtig.';
  String get descriptionMostImportantGoalBeforePractice => 'notiere vor dem Üben dein wichtigstes Tagesziel.';
  String get descriptionNoteMusclesUsedAndRelax => 'notiere, welche Muskeln du für Tonhöhe oder Lautstärke einsetzt und welche du entspannen könntest.';
  String get descriptionPlanTwentyMinuteSessions => 'plane 20-minütige Übeeinheiten mit konkreten Aufgaben/Zielen. Schaffst du es vor dem Alarm? Wenn nicht, stoppe beim Alarm und passe deinen Plan an.';
  String get descriptionPlanTwentyFiveMinuteIntervals => 'plane deine Übezeit im Voraus in 25-Minuten-Intervallen und schreibe auf, was du in jeweils 25 Minuten erreichen möchtest. Mach nach jeder Einheit mindestens 5 Minuten Pause.';
  String get descriptionStartSuccessJournal => 'beginne ein Erfolgstagebuch: Schreibe auf, was du gelernt und erreicht hast. Lege eine Zeit dafür fest – z. B. nach dem Üben, währenddessen oder abends nach dem Essen.';
  String get descriptionWriteDownGoalsForTomorrow => 'schreibe am Ende deine Ziele für morgen auf. Was möchtest du erreicht haben?';
  String get descriptionWriteDownObstaclesAndFocusOne => 'schreibe deine Hindernisse auf, wähle das wichtigste und übe es intensiver.';
  String get descriptionWriteDownWhatDoAndLearnToday => 'schreibe auf, was du heute tun und lernen wirst.';
