import 'package:tiomusic/l10n/flash_cards/flash_cards_localization.dart';

mixin EnglishFlashCards on Object implements FlashCardsLocalization {
  String get categoryCulture => 'Culture';
  String get categoryJournaling => 'Journaling';
  String get categoryMixUp => 'Mix-Up';
  String get categoryPracticingTactics => 'Practicing Tactics';
  String get categoryRelaxation => 'Relaxation';
  String get categorySelfCare => 'Self-Care';
  String get categoryTeam => 'Team';
  String get categoryVision => 'Vision';

  // --- Relaxation ---
  String get descriptionBreathOutThreeTimesDeeply => 'before entering the practice room, breathe out three times as deeply as you can.';
  String get descriptionEvery10MinutesShortBreakAndBeauty => 'every 10 minutes, take a short break to drink a sip of water and discover something around you that you find beautiful.';
  String get descriptionMeditateFiveMinutesBeforePlaying => 'meditate in the practice room for 5 minutes before you start playing your instrument.';
  String get descriptionCloseEyesScanBodyReleaseTensionBuildBreakes => 'pick up your instrument and close your eyes. Feel your entire body, from your toes to the top of your head, and release unnecessary tension. Build breakes into your piece to relax.';
  String get descriptionDefineDaysOffAndCelebrateExceedingPlan => "think about how many days off you'd need before you wanted to practice again. From now on, define that number of days as days off. For every day off that you still practice, you've exceeded your plan — congratulations!";
  String get descriptionPracticeWithoutGoalsDoJustThat => "allow yourself to not have to accomplish anything while practicing. What would you do with your instrument if you didn't have to pursue the goals your daily life demands of you? Do just that first. Remember that everything you do, you don't have to do, because you don't have to do anything, ever.";
  String get descriptionListenFirstDecidePlayOrListen => 'listen to your piece first. Now decide whether you want to try it on the instrument or just listen to it today. You can listen to it several times.';
  String get descriptionReduceStressByShakingStompingLying => 'reduce existing stress by shaking, stomping or lying down for 5 minutes. Do whatever comes to mind intuitively.';
  String get descriptionLookAroundWithEyes60Seconds => 'look around as fast as you can (only) with your eyes for 60 seconds.';
  String get descriptionHumSoundsAndFeelThem => 'hum sounds from time to time and feel them (physically).';
  String get descriptionSitAndDoNothingFiveMinutes => 'first, sit down in the practice room and do nothing for 5 minutes.';
  String get descriptionEyeExerciseLookDirections => 'do this eye exercise: look left, right, up, and down several times. Your eyes will thank you!';
  String get descriptionPayAttentionToBreathing => 'pay particular attention to your breathing.';
  String get descriptionRecordPhraseWatchForPreStartMovements => 'record a phrase on video and, while watching it, pay attention to any unnecessary movements right before you start playing.';
  String get descriptionRecordPlayingWatchFacialExpressions => 'record your playing on video and, while watching it, pay special attention to your facial expressions during and between playing.';
  String get descriptionListenToOwnBreathing3Minutes => 'take 3 minutes beforehand and listen to your own breathing.';
  String get descriptionPayAttentionHeartbeat => 'pay attention to your heartbeat, too.';
  String get descriptionSqueezeAllMuscles10SecondsNoticeChange => 'lie down on the floor and squeeze all of your muscles simultaneously as hard as possible for 10 seconds. Do you feel a change from your original state?';

  // --- Team ---
  String get descriptionAskObserveWhilePracticing => 'ask someone to observe you while practicing. What advice can he/she give you?';
  String get descriptionTellObstacleBrainstormTogether => 'tell someone about one of your obstacles and brainstorm solutions together.';
  String get descriptionDescribeGoalThinkWays => 'describe a goal to someone and think about ways to get there.';
  String get descriptionLetSomeoneTakeLead => 'let someone else take the lead. He/She will guide you by giving instructions or demonstrating how to practice.';
  String get descriptionSomeonePlayTonicAsDrone => 'get someone to play the tonic of the phrase as a drone while you practice. Play your piece with open ears.';
  String get descriptionWatchSomeonePracticeInspiresYou => 'watch someone else practice and write down what inspires you.';
  String get descriptionAskSomeonePlayChamberMusic => 'ask someone (you like) to play chamber music with you.';
  String get descriptionPlayPieceWithSomeone => 'play your piece with someone.';
  String get descriptionChatAboutAversionToPracticing => 'chat with somebody about what to do if you feel an aversion to practicing.';
  String get descriptionHandsOnShouldersReleaseTension => 'have someone else place their hands on your shoulders. Together, pay attention to where you can still release tension.';
  String get descriptionAskSomeoneDistractYou => 'ask someone to distract you while you play.';
  String get descriptionAskSomeoneFastForwardRecording => 'ask someone to fast-forward a bit when playing along to a recording.';
  String get descriptionAskSomeonePressKeysOrOneHand => 'depending on your instrument, ask someone to press the keys while you blow into your instrument, or have someone play one hand while you play the other.';
  String get descriptionAskSomeoneListen15MinutesNoComment => 'ask someone to listen to you practicing for 15 minutes without commenting.';
  String get descriptionTellFriendWhatHowPracticed => 'afterwards, tell a friend what and how you practiced today. Does he/she have any tips for you?';
  String get descriptionPracticeWithFriend => 'practice together with a friend.';
  String get descriptionRecordVideoSendToFriend => 'record a video of yourself and send it to a friend.';
  String get descriptionPlayKnownPieceForSomeoneYouLike => 'play a piece or song that you know well for someone you like.';

  // --- Self-Care ---
  String get descriptionStartPracticeWithPhysicalExercises => 'start your practice with physical exercises: a tapping massage, kneading the neck, grounding, stretching.';
  String get descriptionAskHowFeelingExpressEmotion => 'ask yourself: How am I feeling? Express this emotion on your instrument or with your voice. Improvise, simply make some sounds, or play a suitable piece.';
  String get descriptionAskWhatToExpressEmbodyIntuitively => 'ask yourself: What do I want to express? Who am I, and what do I want to give to the world? Embody this spontaneously and intuitively on your instrument or with your voice. Improvise, make some sounds, or play a suitable piece.';
  String get descriptionTenSecondBreakWhenGettingWorse => 'take a 10-second break if you notice the part that you are working on is getting worse again. (Your brain learns during breaks!)';
  String get descriptionPayAttentionToBodyPartsList => 'pay attention to your body: Write down body parts (neck, shoulders, stomach, feet, spine...) on a piece of paper and pay attention to the different areas one after the other during a phrase.';
  String get descriptionThinkWhichBookTonight => 'think about which book you want to read tonight before going to sleep.';
  String get descriptionBookHelpsPracticeGetAndRead => 'think about which book could help you the most with your practice. Get it and read it.';
  String get descriptionPlanIdealBedtimeToday => 'plan your ideal bedtime for today.';
  String get descriptionPlanWhenFinishWorkToday => 'plan when you want to finish work today.';
  String get descriptionPracticeTimeWithoutDigitalDevices => 'consider how you can spend your practice time without digital devices.';
  String get descriptionDrinkHalfLiterWaterBeforehand => 'drink half a liter of water beforehand.';
  String get descriptionAskSleptInIfNotTryTomorrow => 'ask yourself if you were able to sleep in today. If not, try tomorrow.';
  String get descriptionBiteIntoLemonFirst => 'bite into a lemon first.';
  String get descriptionDrinkGingerShotOrEatRaw => 'drink a ginger shot or eat raw ginger.';
  String get descriptionGuidedMeditationBeforehand => 'do a guided meditation beforehand.';
  String get descriptionConsiderMealEnergizedOrTiring => 'consider which meal makes you feel energized and which meal makes you feel too tired to exercise.';
  String get descriptionConsiderStartedDayWithExercise => 'consider whether you started the day with exercise. If not, try to do so tomorrow.';
  String get descriptionWalkWithoutElectronicDevices => 'take a walk without electronic devices. Your brain needs boredom to clear its mind. Allow your thoughts to flow and simply observe them.';
  String get descriptionTimeForYourselfBeforePerformance => 'think about how you can take time for yourself several hours before your next performance and make sure that you feel comfortable.';
  String get descriptionMeetNeedsBeforeOrDuringBreaks => 'make sure you have your needs met before practicing or meet them during breaks.';
  String get descriptionNoticeHeadFullThenBreak => 'notice when your head is full and then take a break.';
  String get descriptionPlanDayOffEnterInCalendar => 'plan when you will take a day off (recommendation: once a week) and enter it in red in your calendar.';
  String get descriptionSwitchPhoneToAirplaneOrTurnOffInternet => 'first, switch your smartphone to airplane mode, or turn off the internet.';
  String get descriptionWriteDownExtraneousThoughts => 'write down all your extraneous thoughts before and during your practicing.';
  String get descriptionStartWithVeryLongNotesDynamics => 'start with very long notes (drums: rolls). At least one of these should be dynamically constant, one with crescendo, one with diminuendo, and one both dynamically ascending and then descending again.';
  String get descriptionEndPlayFavoritePieceOrSong => 'at the end, play your favorite piece or song.';
  String get descriptionFiveMinuteBreakHalfwayPractice => 'take a 5-minute break halfway through your practice and use it to do things like drink a glass of water, get some fresh air, or exercise.';
  String get descriptionRoomTidyIfNotTidyUp => 'look around: is your room tidy? If not, can you tidy it up with little effort?';
  String get descriptionWherePracticeEffectivelyHomeOrElse => 'consider where you can practice effectively - at home or somewhere else? Do you prefer to practice in the same place, or do you benefit from variety?';
  String get descriptionSeeHowYouCanHaveFun => 'see how you can have fun.';

  // --- Vision ---
  String get descriptionLieDownUntilKnowWhyGetUp => 'lie down and stay there until you know exactly why it is worth it for you to get up and pick up your instrument.';
  String get descriptionThinkWhyGetUpEarlyTomorrow => "think about why you might get up early tomorrow. What would be so stimulating that you would wake up before the alarm because you're so excited about it?";
  String get descriptionWriteDownDailyGoalFirstHalf => 'write down a daily goal. Something you can easily achieve and try to accomplish in the first half of the day. This will make the second half feel much freer.';
  String get descriptionMirrorGiveMotivationalSpeech => 'stand in front of a mirror and give yourself a motivational speech in which you enthusiastically tell yourself what you have already achieved, how proud you are of yourself, why this path is the right one for you and what makes your eyes light up.';
  String get descriptionWriteDownEuphoricMomentsFromPast => 'write down key euphoric moments from the past. Do they have anything in common? What exactly made your eyes light up?';
  String get descriptionPracticeWithGoalInMind => 'think about your goal beforehand, and practice with this goal in mind.';

  // --- Culture ---
  String get descriptionBookConcertTicket => "book a ticket for a concert you're really looking forward to!";
  String get descriptionRewardYourselfWhateverThinkOf => 'reward yourself with whatever you can think of.';
  String get descriptionCreatePrivateConcertFeelGoodOasis => "create your own private concert by watching or listening to a great recording of a piece that's important to you. Make yourself comfortable and create a feel-good oasis.";
  String get descriptionMeetFriendsListenShareFavorites => 'make plans to meet up with friends to listen to music and afterwards tell each other about your favorite moments and what excited you.';
  String get descriptionDiscoverNewPieceManyWays => 'discover a piece that is new to you in as many ways as possible – listen to it, move to it, read the context in which it was written or performed, draw a picture, improvise on a theme...';
  String get descriptionRediscoverKnownPieceManyWays => 'rediscover a piece you already know in as many ways as possible – listen to it, move to it, read the context in which it was written or performed, draw a picture, improvise on a theme...';
  String get descriptionListenRoleModelWriteWhatToLearn => 'listen to music by one of your role models and write down what you would like to learn from that person.';
  String get descriptionWriteTwoMinuteIntroductionForPiece => 'write a 2-minute introduction for your current piece.';
  String get descriptionWriteIntroForTwoTargetGroups => 'write an introduction to your current piece for two different target groups, for example, classical concert-goers, school classes, senior citizens, football fans, etc.';
  String get descriptionReadBerliozTreatiseSectionOnYourInstrument => 'if available, read the section on your instrument in Hector Berlioz\'s "Treatise of Instrumentation", in the revised version by Richard Strauss (1905).';
  String get descriptionReadWikipediaArticleAboutInstrument => 'read the Wikipedia article about your instrument.';

  // --- Mix-Up ---
  String get descriptionWalkInNatureOutsideHelpUnderstand => 'go for a walk in nature. What can you do outside without your instrument that will help you better understand your piece?';
  String get descriptionShortPowerNapVisualizePieceAsleep => 'take time for a short power nap (max. 30 minutes). Play your piece in your head as you fall asleep, and be surprised by what happens next while you sleep.';
  String get descriptionConsiderPieceListenThreeTimesExpressBeauty => "consider which piece, recording or song you currently want to listen to. Listen to it three times and try to figure out what appeals to you about it. Try to express the beauty you've found through your current piece.";
  String get descriptionListThingsRatherDoIncorporate => "make a list of things you'd rather do than practice. Also note what you enjoy about those activities. Incorporate them into your practice!";
  String get descriptionWriteNotToDoList => "write a not-to-do list. Write down all the things you don't want to do today and check them off in the evening if they actually haven't been done.";
  String get descriptionLaughInSecludedPlace => 'go to a secluded place (e.g., the basement) and laugh. Lie on your side and let your stomach bounce. Think of something funny or listen to something.';
  String get descriptionTreatInstrumentLikePet => 'treat your instrument like a pet. What kind of affection would you give it? What would you call it?';
  String get descriptionDoWhatMakesYouHappyIn10Minutes => 'think about what would make you truly happy if you accomplished it today. Try to do it in 10 minutes of practicing! What do you need to do to make it happen?';
  String get descriptionListenToNeinhornSayNo => 'listen to "Neinhorn" by Marc-Uwe Kling or read it (at the library!). What would you like to say "no" to? Go for it!';
  String get descriptionInstallDroneAppPlayOver => 'install an app that allows you to create a drone, and play long tones, slow scales or chords over the drone.';
  String get descriptionWriteDownPracticeIdeasForFun => 'write down all the ideas you have for practicing to make it more fun. Imagine how many ideas you would collect if you repeat this for a week!';
  String get descriptionSingScaleInTune => 'practice singing a scale in tune. Play the root on the piano and sing the scale up and down.';
  String get descriptionSingTriadInTune => 'practice singing a triad in tune. Play the root on the piano and sing the triad up and down.';
  String get descriptionLearnFingerCoordinationExercise => 'look up finger-coordination excercises online and learn one. After a week, test how playing feels before and after doing the excercise.';
  String get descriptionPlayWhileBalancing => 'play or sing your piece while standing on one leg or on a balance board. What do you perceive?';
  String get descriptionDrawEmoticonsForSections => 'draw appropriate emoticons for the different sections of your piece.';
  String get descriptionPlanFavoriteSportAsReward => 'plan to do your favorite sport as a reward after practicing today.';
  String get descriptionWhatWouldTeacherSay => 'think about what your teacher would say about the phrases you practice.';
  String get descriptionIncorporateAndCelebrateMistakes => 'consciously incorporate mistakes into your piece and celebrate them vigorously.';
  String get descriptionAnalyzeSectionOfPiece => 'analyze a section of your piece.';
  String get descriptionLookAtFullScoreBeyondPart => 'look at the score to see what is happening beyond your part.';
  String get descriptionConductYourPiece => 'conduct your piece (in front of a mirror if you like).';
  String get descriptionChangePlaceMethodOrPieceWhenBored => 'if you sometimes get bored or your mind wanders, change the place, method or piece.';
  String get descriptionThrowRulesOverboardPlayFreely => 'allow yourself to throw all the rules overboard and just play freely today.';
  String get descriptionPlayWithEyesClosed => 'play a phrase with your eyes closed.';
  String get descriptionIncorporateWhatYouReallyWant => 'think about what you really want to do. Would it be possible to incorporate it into your day today?';
  String get descriptionListenToSoundThenBody => 'listen to your sound once and then listen to your body.';
  String get descriptionDrawYourselfAndInstrument => 'draw yourself and your instrument in action. What do you notice about your posture, proportions, details, etc.?';
  String get descriptionDarkenRoomOrCoverEyes => 'darken the room or cover your eyes before you start.';
  String get descriptionPlayOnTiptoes => 'play a phrase on tiptoes.';
  String get descriptionAttendToArtisticCharisma => 'pay special attention to your artistic charisma or persuasiveness.';
  String get descriptionPlayWithMostBeautifulSound => 'play consistently with the most beautiful sound possible.';
  String get descriptionAskCouldItSoundBetter => 'after playing a phrase, ask yourself: Could it sound even better?';
  String get descriptionCreateOwnPracticeTip => 'create your own practice tip and then try it out.';
  String get descriptionBalanceTensionAndRelaxation => 'find the right balance between healthy tension and relaxation while playing.';
  String get descriptionImagineAudienceParentsConcertHall => 'imagine you are playing in front of an audience (your parents, in a concert hall, etc.).';
  String get descriptionCreateSongFromFewNotes => 'choose three to five notes and use them to create a song.';
  String get descriptionPlayByEar => 'play something you know just by ear.';
  String get descriptionColorCodePieceSections => 'divide the piece into sections and color-code them.';
  String get descriptionPhraseAtMultipleTempos => 'play a phrase once at normal tempo, once at half speed, and once at double speed.';
  String get descriptionInventStoryAboutPiece => 'invent a story about your current piece. If you like, tell it to someone.';
  String get descriptionCutUpAndReassembleScore => 'photocopy your current piece, cut it up and put it back together.';
  String get descriptionComposeWithOnePitch => 'compose a piece using only one pitch.';
  String get descriptionComposeMusicToPicture => 'choose a picture or photo and compose music to it.';
  String get descriptionListAllNotesAndResearchRange => 'take a blank sheet of music paper and write down all the notes you can play. How many are there, and how many octaves do they cover? Research the range intended for your instrument. Is it rather large or small compared to other instruments?';
  String get descriptionChooseAppealingPracticeTip => 'look through the practice tips and choose one that appeals to you and that you would like to do today.';
  String get descriptionClapRhythmWithMetronome => 'clap the rhythm of a phrase with the metronome.';
  String get descriptionFollowScoreWhileListening => 'listen to a recording of your piece and follow your part at the same time, or alternatively follow the score.';
  String get descriptionSingFingerOrPlayAlongRecording => 'listen to a recording of your piece and sing along, finger along silently, or play along.';
  String get descriptionFindPlayalongTrack => 'research whether there is a playalong track for your piece and if so, play along.';
  String get descriptionPlayAsQuietlyAsPossible => 'allow the sound to come out of your instrument as quietly as possible.';
  String get descriptionThreeNotesAsQuietAsPossible => 'choose three notes and play them as quietly as possible.';
  String get descriptionPracticeInFiveMinuteBursts => 'practice for only 5 minutes at a time (stopwatch) and then change your practice goal or method so that you learn something different each time.';
  String get descriptionPlayWithImaginaryInstrument => 'play your current piece with an imaginary instrument.';
  String get descriptionDanceToYourPiece => 'dance to your piece.';
  String get descriptionAlternatePlayAndThinkSingBars => 'alternate between playing one bar and thinking or singing the next bar.';
  String get descriptionPlayPieceToProgress => 'play a piece that will help you progress musically.';
  String get descriptionChangePracticeLocation => 'change your practice location: change the room, turn to face a different direction, etc.';
  String get descriptionWritePhraseFromMemoryCompare => 'write down a phrase from memory on music paper and compare it with the original.';
  String get descriptionPlayAtPulseTempo => 'play your piece at the tempo of your pulse.';
  String get descriptionImagineFilmSceneForPiece => 'imagine your piece was film music. What would the scene look like?';
  String get descriptionVisualizeNextConcert => 'visualize your next concert.';
  String get descriptionImproviseOnMainMotif => 'improvise on the main motif of your piece for 5 minutes.';
  String get descriptionStandByWallHearReflectedSound => 'if possible, stand in front of a wall so that you can directly hear your reflected sound.';
  String get descriptionFindAdjectiveForSections => 'find at least one suitable adjective for every section of your piece and write it in the music.';
  String get descriptionFindImageForSections => 'find at least one suitable image for every section of your piece (sunrise, forest, sparkling mineral water, etc.). If you like, search for suitable images online.';

  // --- Practicing Tactics ---
  String get descriptionPracticeVarietyWriteStrategies => 'practice in a variety of ways. Write down all the practice strategies you use spontaneously and use them for other pieces as well.';
  String get descriptionPlayAlongWithRecording => 'play along with a recording.';
  String get descriptionPlayPartOnPianoIfPianoSing => 'play your part on the piano. If your instrument is the piano, sing.';
  String get descriptionFlutterTongueCorkOrSimilar => 'play your piece with flutter tongue, sing your piece while biting on a cork, or think of something similar for your instrument.';
  String get descriptionSingAPhrase => 'sing a phrase.';
  String get descriptionSingThroughChangingOctaves => 'sing through your piece changing octaves when necessary.';
  String get descriptionFindChallengingPartLearnToSing => 'find the most challenging part of the piece and learn to sing it. For singers: learn to sing the accompaniment, while singing your part mentally.';
  String get descriptionWhistleYourPiece => 'whistle your piece.';
  String get descriptionPassageSilentlyOrWhisper => 'play a passage silently or whisper the passage if you are a singer.';
  String get descriptionPassageDifferentTemposSlowFast => 'play a passage at different tempos - sometimes very slowly, sometimes very quickly.';
  String get descriptionTransposePassageThreeKeys => 'transpose a passage into three other keys.';
  String get descriptionPracticeChallengingSectionLoop => 'practice a challenging section in a loop.';
  String get descriptionStartRandomPlaceMiddle => 'start at a random place somewhere in the middle of your piece, phrase or passage.';
  String get descriptionPlayExtremelyQuietOrLoud => 'play extremely quietly or excessively loudly.';
  String get descriptionIncorporateUnusualAccents => 'incorporate unusual accents into your piece.';
  String get descriptionPlayStandingBalancingClosedEyes => 'play standing on one leg, squatting, or balancing on a balance board. Next level: with closed eyes.';
  String get descriptionExaggerateGivenArticulation => 'exaggerate the given articulation.';
  String get descriptionJumpIntoMiddleRecordingStartPlaying => 'jump into the middle of a recording of your piece and listen for just two seconds. At this point, ideally in the middle of the phrase, start playing.';
  String get descriptionPlayInSuboptimalStateShineFirstTry => 'play the passage in a suboptimal state: not warmed-up, in a midday slump, etc. Shine on the first try!';
  String get descriptionExpandRangeOfPassage => 'expand the range of a passage.';
  String get descriptionPlayCertainNotesOctaveHigherLower => 'play certain notes of a passage an octave higher or lower.';
  String get descriptionRecord10SecondsListenImprove => 'record yourself for 10 seconds and listen to the recording immediately. What are you satisfied with, and what would you improve?';
  String get descriptionRecommendExerciseToBestFriend => 'which exercise would you recommend to your best friend?';
  String get descriptionLeanAgainstWallObserveShouldersHead => 'lean against a wall or doorframe. Play while observing your shoulders and head, which should be touching the wall. If you notice any tension, release it.';
  String get descriptionPlayStrugglePartInFrontMirror => 'play a part you struggle with in front of the mirror. What happens at the point where you stumble?';
  String get descriptionNoteWithDifferentPianoChordsIntonation => 'play a note on your instrument with different chords on the piano and notice how it feels when it is in tune.';
  String get descriptionRecordPassageHalfSpeedNotice => "record a passage you can't play yet and listen to it at half-speed. What do you notice?";
  String get descriptionPlayVerySlowConsciousRelaxed => 'play very slowly and consciously, in-tune and relaxed. Play so slowly that you always know what you have to do and can hear and feel everything.';
  String get descriptionFingerPassageSilentlyBeforePlaying => 'finger the exercise or passage silently and consciously before playing it.';
  String get descriptionPracticeChallengingFingerings => 'practice using especially challenging fingerings.';
  String get descriptionPayAttentionIntonationSoundPhrasing => 'pay attention to intonation, sound and phrasing when practicing technical exercises.';
  String get descriptionThinkThroughGroupNotesSlowCleanFast => 'take a group of notes, and think through them mentally. Then play them very slowly and cleanly, and then at maximum speed.';
  String get descriptionPracticeDifferentiallyHalfHoleDifferences => "practice differentially. For example with the flute: finger a half-hole that's too big, too small, too crooked... Place a finger too far away, or too close. Work your way through the differences.";
  String get descriptionDivideIntoCharacterSectionsEmotionIntensity => 'divide your piece into character sections and note an emotion and its intensity for each one.';
  String get descriptionRecordShortSectionEmbodyEmotionIrritates => 'record a short section and try to embody an emotion. Listen to the recording: what irritates you?';
  String get descriptionImagineAssociatedSituationFeelIt => 'imagine yourself in a situation that you associate with your piece. While playing, feel exactly how you would in that situation.';
  String get descriptionExaggerateDynamicDifferences => 'exaggerate dynamic differences.';
  String get descriptionExaggerateArticulationDifferences => 'exaggerate differences in articulation.';
  String get descriptionExaggerateAgogics => 'exaggerate agogics.';
  String get descriptionExaggerateTonalDifferences => 'exaggerate tonal differences.';
  String get descriptionMoveToRecordingOfPiece => 'move to a recording of your piece.';
  String get descriptionImagineWithoutInstrumentChangeFacialExpressions => 'imagine playing or singing your piece without an instrument and change your facial expressions in front of the mirror to match.';
  String get descriptionRecordListenMarkMistakesPracticeThose => "record yourself on your phone or a recording device. Listen to the recording immediately, and mark the parts of the music that you didn't quite get right. Then practice those parts exactly.";
  String get descriptionVideoRecordNoteConvincingPracticeThose => "video-record yourself on your phone or a recording device. Watch the recording immediately and note which parts you find convincing and which you don't. Then practice exactly those parts.";
  String get descriptionGoThroughMentallyPartsUnclear => "go through your piece mentally. Which parts aren't so clear yet?";
  String get descriptionSingByHeartNoteNamesPartsNotSmooth => "sing your piece by heart, using the note names. Which parts aren't flowing so smoothly yet?";
  String get descriptionTakeBreakWhenGoingWell => 'always take a break when things are going really well.';
  String get descriptionPracticeFromChallengingToEasiest => 'divide your piece into phrases, and practice from the most challenging to the easiest.';
  String get descriptionPractice5MinutesThen5MinuteBreak => 'practice for 5 minutes, then take a 5 minute break.';
  String get descriptionRecordTrickyPartsSlowMotionAnalyze => "record tricky parts in slow-motion, and analyze what isn't working so well (or watch a video recorded at normal-speed slowed down).";
  String get descriptionPlayAsPhysicallyRelaxedAsPossible => 'play as physically relaxed as you can.';
  String get descriptionImagineSlowlyPlayingBar => 'imagine yourself slowly playing a bar.';
  String get descriptionStopWhenMostFun => "stop right when it's most fun.";
  String get descriptionListenShortPartsThenPlayImmediately => 'listen to short parts of your favorite recording and then play them immediately.';
  String get descriptionAllowYourselfToMakeMistakes => 'allow yourself to make mistakes.';
  String get descriptionIfNotSmoothWriteSolutionsTry => "if something isn't running smoothly, write down possible solutions and try them out.";
  String get descriptionPracticeMostDifficultPhraseFiveTimes => 'practice the most difficult phrase of the piece exactly 5 times, at a tempo where you can play everything correctly.';
  String get descriptionPlayOnOneNotePayAttentionPhrasing => 'play your piece on one note. Pay attention to the phrasing.';
  String get descriptionPractice15MinuteSessionsWithBreaks => 'practice in 15-minute sessions (with a stopwatch), and take a 5-minute break after each session.';
  String get descriptionTimeHowLongPracticeToday => 'time how long you practice today.';
  String get descriptionTryPlayByHeartSeeHowMuchPossible => 'try to play your piece by heart, and just see how much is possible.';
  String get descriptionDeliberatelyPlayFastPassagesSlowly => 'deliberately play fast passages very slowly.';
  String get descriptionDirectAttentionDifferentAspectEachRepetition => 'direct your attention to a different aspect (intonation, sound, etc.) with each repetition of the phrase.';
  String get descriptionPracticeFromEndAddBars => 'practice a phrase from the end: for example, start with the last bar and gradually add more bars.';
  String get descriptionPracticePhraseBackwards => 'practice a phrase backwards.';
  String get descriptionImproveRightNowAnotherLevel => 'what can you improve right now to immediately take the result to another level?';
  String get descriptionPlayNormalQuietLoudRepeatChallenging => 'play a phrase once at normal volume, once very quietly and once very loudly. Repeat the version that challenged you the most four more times.';
  String get descriptionMemorizeBeginningAndEnd => 'memorize the beginning and end of the piece you are practicing.';
  String get descriptionStartWithPieceForUpcomingPerformance => 'start with a piece you need for an upcoming performance.';
  String get descriptionVaryRhythmAtLeastThreeTimes => 'vary the rhythm of your phrase at least three times (for example, dotted, triplet, swing).';
  String get descriptionIncreaseDifficultyForChallengingPhrase => 'look for a challenging phrase and consciously increase the level of difficulty.';
  String get descriptionMemorizeMostChallengingPhrase => 'memorize the most challenging phrase of your piece.';

  // --- Journaling ---
  String get descriptionWriteDownWhatYouWillDoAndLearnToday => 'write down what you will do and learn today.';
