// overriding keys do not change something; ignored because soft warning
// ignore_for_file: annotate_overrides

import 'package:tiomusic/l10n/flash_cards/flash_cards_localization.dart';

mixin EnglishFlashCards on Object implements FlashCardsLocalization {
  String get categoryCulture => 'Culture';
  String get categoryJournaling => 'Journaling';
  String get categoryMixUp => 'Mix-Up';
  String get categoryPracticingTactics => 'Practicing Tactics';
  String get categoryRelaxation => 'Relaxation';
  String get categorySelfCare => 'Self-Care';
  String get categoryTeam => 'Team';
  String get categoryVision => 'Vision';

  // --- Relaxation ---
  String get descriptionAllowNoAccomplishment => "allow yourself to not have to accomplish anything while practicing. What would you do with your instrument if you didn't have to pursue the goals your daily life demands of you? Do just that first. Remember that everything you do, you don't have to do, because you don't have to do anything, ever.";
  String get descriptionBreakWaterBeauty => 'every 10 minutes, take a short break to drink a sip of water and discover something around you that you find beautiful.';
  String get descriptionBreathOutThreeTimes => 'before entering the practice room, breathe out three times as deeply as you can.';
  String get descriptionCloseEyesReleaseTension => 'pick up your instrument and close your eyes. Feel your entire body, from your toes to the top of your head, and release unnecessary tension. Build breakes into your piece to relax.';
  String get descriptionDaysOffCongratulations => "think about how many days off you'd need before you wanted to practice again. From now on, define that number of days as days off. For every day off that you still practice, you've exceeded your plan — congratulations!";
  String get descriptionEyeExerciseDirections => 'do this eye exercise: look left, right, up, and down several times. Your eyes will thank you!';
  String get descriptionEyeScanSixtySeconds => 'look around as fast as you can (only) with your eyes for 60 seconds.';
  String get descriptionHumAndFeelSounds => 'hum sounds from time to time and feel them (physically).';
  String get descriptionListenFirstThenDecide => 'listen to your piece first. Now decide whether you want to try it on the instrument or just listen to it today. You can listen to it several times.';
  String get descriptionListenToBreathingThreeMinutes => 'take 3 minutes beforehand and listen to your own breathing.';
  String get descriptionMeditateFiveMinBefore => 'meditate in the practice room for 5 minutes before you start playing your instrument.';
  String get descriptionPayAttentionHeartbeat => 'pay attention to your heartbeat, too.';
  String get descriptionPayAttentionToBreathing => 'pay particular attention to your breathing.';
  String get descriptionRecordPhraseWatchMovements => 'record a phrase on video and, while watching it, pay attention to any unnecessary movements right before you start playing.';
  String get descriptionRecordPlayingWatchFacialExpressions => 'record your playing on video and, while watching it, pay special attention to your facial expressions during and between playing.';
  String get descriptionReduceStressShakeStompLieDown => 'reduce existing stress by shaking, stomping or lying down for 5 minutes. Do whatever comes to mind intuitively.';
  String get descriptionSitAndDoNothingFiveMinutes => 'first, sit down in the practice room and do nothing for 5 minutes.';
  String get descriptionSqueezeMusclesTenSeconds => 'lie down on the floor and squeeze all of your muscles simultaneously as hard as possible for 10 seconds. Do you feel a change from your original state?';

  // --- Team ---
  String get descriptionAskDistractionWhilePlaying => 'ask someone to distract you while you play.';
  String get descriptionAskFastForwardDuringPlayback => 'ask someone to fast-forward a bit when playing along to a recording.';
  String get descriptionAskSomeoneToObserveYou => 'ask someone to observe you while practicing. What advice can he/she give you?';
  String get descriptionChatAboutAversionToPracticing => 'chat with somebody about what to do if you feel an aversion to praticing.';
  String get descriptionDescribeGoalTogether => 'describe a goal to someone and think about ways to get there.';
  String get descriptionHandsOnShouldersReleaseTension => 'have someone else place their hands on your shoulders. Together, pay attention to where you can still release tension.';
  String get descriptionLetSomeoneElseLead => 'let someone else take the lead. He/She will guide you by giving instructions or demonstrating how to practice.';
  String get descriptionListenWithoutCommentFifteenMinutes => 'ask someone to listen to you practicing for 15 minutes without commenting.';
  String get descriptionPlayChamberMusicTogether => 'ask someone (you like) to play chamber music with you.';
  String get descriptionPlayForSomeoneYouLike => 'play a piece or song that you know well for someone you like.';
  String get descriptionPlayWithDroneSupport => 'get someone to play the tonic of the phrase as a drone while you practice. Play your piece with open ears.';
  String get descriptionPlayWithSomeone => 'play your piece with someone.';
  String get descriptionPracticeWithFriend => 'practice together with a friend.';
  String get descriptionSendVideoToFriend => 'record a video of yourself and send it to a friend.';
  String get descriptionSplitHandsOrPressKeysTogether => 'depending on your instrument, ask someone to press the keys while you blow into your instrument, or have someone play one hand while you play the other.';
  String get descriptionTellAFriendAndAskForTips => 'afterwards, tell a friend what and how you practiced today. Does he/she have any tips for you?';
  String get descriptionTellObstacleAndBrainstorm => 'tell someone about one of your obstacles and brainstorm solutions together.';
  String get descriptionWatchSomeonePracticeWriteInspiration => 'watch someone else practice and write down what inspires you.';

  // --- Self-Care ---
  String get descriptionBiteIntoLemon => 'bite into a lemon first.';
  String get descriptionBodyPartsAwarenessList => 'pay attention to your body: Write down body parts (neck, shoulders, stomach, feet, spine...) on a piece of paper and pay attention to the different areas one after the other during a phrase.';
  String get descriptionBookForTonight => 'think about which book you want to read tonight before going to sleep.';
  String get descriptionBookToHelpPractice => 'think about which book could help you the most with your practice. Get it and read it.';
  String get descriptionConsiderEffectivePracticeLocation => 'consider where you can practice effectively - at home or somewhere else? Do you prefer to practice in the same place, or do you benefit from variety?';
  String get descriptionDrinkHalfLiterWater => 'drink half a liter of water beforehand.';
  String get descriptionEndWithFavoritePiece => 'at the end, play your favorite piece or song.';
  String get descriptionExpressHowYouFeel => 'ask yourself: How am I feeling? Express this emotion on your instrument or with your voice. Improvise, simply make some sounds, or play a suitable piece.';
  String get descriptionExpressWhatYouWantToGive => 'ask yourself: What do I want to express? Who am I, and what do I want to give to the world? Embody this spontaneously and intuitively on your instrument or with your voice. Improvise, make some sounds, or play a suitable piece.';
  String get descriptionGingerShotOrRaw => 'drink a ginger shot or eat raw ginger.';
  String get descriptionGuidedMeditationBeforehand => 'do a guided meditation beforehand.';
  String get descriptionHalfwayFiveMinuteBreak => 'take a 5-minute break halfway through your practice and use it to do things like drink a glass of water, get some fresh air, or exercise.';
  String get descriptionMeetNeedsBeforeOrDuringBreaks => 'make sure you have your needs met before practicing or meet them during breaks.';
  String get descriptionNoticeHeadIsFullTakeBreak => 'notice when your head is full and then take a break.';
  String get descriptionPlanDayOffInCalendar => 'plan when you will take a day off (recommendation: once a week) and enter it in red in your calendar.';
  String get descriptionPlanFinishWorkToday => 'plan when you want to finish work today.';
  String get descriptionPlanIdealBedtime => 'plan your ideal bedtime for today.';
  String get descriptionPracticeWithoutDevices => 'consider how you can spend your practice time without digital devices.';
  String get descriptionPrepareHoursBeforePerformance => 'think about how you can take time for yourself several hours before your next performance and make sure that you feel comfortable.';
  String get descriptionSeeHowYouCanHaveFun => 'see how you can have fun.';
  String get descriptionSleepInIfPossible => 'ask yourself if you were able to sleep in today. If not, try tomorrow.';
  String get descriptionStartDayWithExercise => 'consider whether you started the day with exercise. If not, try to do so tomorrow.';
  String get descriptionStartWithLongNotes => 'start with very long notes (drums: rolls). At least one of these should be dynamically constant, one with crescendo, one with diminuendo, and one both dynamically ascending and then descending again.';
  String get descriptionStartWithPhysicalExercises => 'start your practice with physical exercises: a tapping massage, kneading the neck, grounding, stretching.';
  String get descriptionSwitchToAirplaneMode => 'first, switch your smartphone to airplane mode, or turn off the internet.';
  String get descriptionTenSecondBreakIfWorse => 'take a 10-second break if you notice the part that you are working on is getting worse again. (Your brain learns during breaks!)';
  String get descriptionTidyRoomIfPossible => 'look around: is your room tidy? If not, can you tidy it up with little effort?';
  String get descriptionWalkWithoutDevices => 'take a walk without electronic devices. Your brain needs boredom to clear its mind. Allow your thoughts to flow and simply observe them.';
  String get descriptionWhichMealEnergizes => 'consider which meal makes you feel energized and which meal makes you feel too tired to exercise.';
  String get descriptionWriteDownExtraneousThoughts => 'write down all your extraneous thoughts before and during your practicing.';

  // --- Vision ---
  String get descriptionLieDownUntilMotivated => 'lie down and stay there until you know exactly why it is worth it for you to get up and pick up your instrument.';
  String get descriptionMirrorMotivationalSpeech => 'stand in front of a mirror and give yourself a motivational speech in which you enthusiastically tell yourself what you have already achieved, how proud you are of yourself, why this path is the right one for you and what makes your eyes light up.';
  String get descriptionPracticeWithGoalInMind => 'think about your goal beforehand, and practice with this goal in mind.';
  String get descriptionThinkWhyGetUpEarly => "think about why you might get up early tomorrow. What would be so stimulating that you would wake up before the alarm because you're so excited about it?";
  String get descriptionWriteDownDailyGoal => 'write down a daily goal. Something you can easily achieve and try to accomplish in the first half of the day. This will make the second half feel much freer.';
  String get descriptionWriteDownEuphoricMoments => 'write down key euphoric moments from the past. Do they have anything in common? What exactly made your eyes light up?';

// --- Culture ---
  String get descriptionBookConcertTicket => "book a ticket for a concert you're really looking forward to!";
  String get descriptionCreatePrivateConcert => "create your own private concert by watching or listening to a great recording of a piece that's important to you. Make yourself comfortable and create a feel-good oasis.";
  String get descriptionDiscoverNewPieceInManyWays => 'discover a piece that is new to you in as many ways as possible – listen to it, move to it, read the context in which it was written or performed, draw a picture, improvise on a theme...';
  String get descriptionListenToRoleModelAndLearn => 'listen to music by one of your role models and write down what you would like to learn from that person.';
  String get descriptionPlanMeetFriendsListenMusic => 'make plans to meet up with friends to listen to music and afterwards tell each other about your favorite moments and what excited you.';
  String get descriptionReadBerliozTreatiseSection => 'if available, read the section on your instrument in Hector Berlioz\'s "Treatise of Instrumentation", in the revised version by Richard Strauss (1905).';
  String get descriptionReadWikipediaAboutInstrument => 'read the Wikipedia article about your instrument.';
  String get descriptionRediscoverPieceInManyWays => 'rediscover a piece you already know in as many ways as possible – listen to it, move to it, read the context in which it was written or performed, draw a picture, improvise on a theme...';
  String get descriptionRewardYourself => 'reward yourself with whatever you can think of.';
  String get descriptionWriteIntroForTwoTargetGroups => 'write an introduction to your current piece for two different target groups, for example, classical concert-goers, school classes, senior citizens, football fans, etc.';
  String get descriptionWriteTwoMinuteIntroduction => 'write a 2-minute introduction for your current piece.';



  String flashCardDescriptionById(String id) => switch (id) {
    'descriptionAskSomeoneToObserveYou' => descriptionAskSomeoneToObserveYou,
    'descriptionBreakWaterBeauty' => descriptionBreakWaterBeauty,
    'descriptionBreathOutThreeTimes' => descriptionBreathOutThreeTimes,
    'descriptionCloseEyesReleaseTension' => descriptionCloseEyesReleaseTension,
    'descriptionDaysOffCongratulations' => descriptionDaysOffCongratulations,
    'descriptionDescribeGoalTogether' => descriptionDescribeGoalTogether,
    'descriptionLetSomeoneElseLead' => descriptionLetSomeoneElseLead,
    'descriptionMeditateFiveMinBefore' => descriptionMeditateFiveMinBefore,
    'descriptionPlayWithDroneSupport' => descriptionPlayWithDroneSupport,
    'descriptionTellObstacleAndBrainstorm' => descriptionTellObstacleAndBrainstorm,
    _ => id,
  };

  String get flashCardTitle => 'When you practice today,';

  String get flashCardsPageTitle => 'Practice tips';
}
